import { useState } from "react";

import {FormGroup, FormControl, InputLabel , Input, Typography, styled, Button} from "@mui/material";

const Container = styled(FormGroup)`
width:50%;
margin: 5% auto 0 auto;
& > div {
    margin-top: 20px;
}
`

const defaultValue ={
    name: '',
    username: '',
    email: '',
    phone: '',
}

const AddUser = () => {

    const [user, setUser] = useState({defaultValue});

    const onValueChange = (event) => {
        console.log(event.target.name ,event.target.value)
        setUser ({ ...user, [event.target.name]: event.target.value});
        console.log(user);
    }
const addUserHandle = async () => {
    await addUser(user);
}

    return (
        <Container>
            <Typography variant="h1"> Add User</Typography>
            <FormControl>
                <InputLabel>Name</InputLabel>
                <Input onChange={(event) => onValueChange(event)} name="name"/>
            </FormControl>
            <FormControl>
                <InputLabel>UserName</InputLabel>
                <Input onChange={(event) => onValueChange(event)} name="username"/>
            </FormControl>
            <FormControl>
                <InputLabel>Email</InputLabel>
                <Input onChange={(event) => onValueChange(event)} name="email"/>
            </FormControl>
            <FormControl>
                <InputLabel>Phone</InputLabel>
                <Input onChange={(event) => onValueChange(event)} name="phone"/>
            </FormControl>
            <FormControl>
                <Button variant="contained" onClick={addUserHandler}>Add User</Button>
            </FormControl>
        </Container>
    )
}

export default AddUser;
