import { useState, useEffect } from "react";

import {FormGroup, FormControl, InputLabel , Input, Typography, styled, Button} from "@mui/material";

import { addUser, getUser } from "../service/api";
import { useNavigate, useParams} from 'react-router-dom';

const Container = styled(FormGroup)`
width:50%;
margin: 5% auto 0 auto;
& > div {
    margin-top: 20px;
}
`

const initialValue ={
    name: '',
    username: '',
    email: '',
    phone: '',
}

const EditUser = () => {

    const [user, setUser] = useState({initialValue});

    const navigate = useNavigate();

    const { id } = useParams();

    useEffect(() => {
        loadUserDetails();
    })

    const loadUserDetails = async() => {
        const response = await getUser(id);
        setUser(response.data);
    }

    const onValueChange = (event) => {
        //console.log(event.target.name ,event.target.value)
        setUser ({ ...user, [event.target.name]: event.target.value});
        //console.log(user);
    }
    
const addUserHandler = async  () => {
     await addUser(user);
     navigate('/all');
}

    return (
        <Container>
            <Typography variant="h4"> Edit User</Typography>
            <FormControl>
                <InputLabel>Name</InputLabel>
                <Input onChange={(event) => onValueChange(event)} name="name" value={user.name}/>
            </FormControl>
            <FormControl>
                <InputLabel>UserName</InputLabel>
                <Input onChange={(event) => onValueChange(event)} name="username"  value={user.username}/>
            </FormControl>
            <FormControl>
                <InputLabel>Email</InputLabel>
                <Input onChange={(event) => onValueChange(event)} name="email"  value={user.email}/>
            </FormControl>
            <FormControl> 
                <InputLabel>Phone</InputLabel>
                <Input onChange={(event) => onValueChange(event)} name="phone"  value={user.phone}/>
            </FormControl>
            <FormControl>
                <Button variant="contained" onClick={() => addUserHandler()}>Edit User</Button>
            </FormControl>
        </Container>
    )
}

export default EditUser;
